$(document).ready(function(){$("html").niceScroll();$("#no-more-posts").hide();$(".post-title").each(function(){var a=$(this).html().split(" ").join("-");$(this).html(a)});$("#load-more").click(function(){$.ajax({type:"POST",url:"/get_more_posts",dataType:"json",success:function(a){0<a.posts.length?(a=$(a.posts),$("#post-content").append(a).masonry("appended",a)):$("#load-more").fadeOut(function(){$("#no-more-posts").fadeIn()})},error:function(a){console.log(a.responseJSON.error)}})})});
$(window).on("load",function(){$("#post-content").masonry({itemSelector:"article",transitionDuration:"0.2s"})});$(window).resize(function(){768>jQuery(window).innerWidth()?jQuery("#post-content").masonry("destroy"):jQuery("#post-content").masonry({itemSelector:"article",transitionDuration:"0.2s"})});